var result = false;  

if (!userinfo.right)   
  return false;  

switch (right) { 
  case 'generic.page.userPage':     
    result = userinfo.right.includes('identity.user.view');     
    break; 
  case 'generic.page.rolePage':     
    result = userinfo.right.includes('identity.role.view');     
    break; 
  case 'generic.page.documentPage':     
    result = userinfo.right.includes('meta.document.view');     
    break; 
  case 'generic.page.tenantPage':     
    result = userinfo.right.includes('meta.tenant.view');     
    break; 
  case 'generic.page.entityPage':     
    result = userinfo.right.includes('meta.entity.view');     
    break; 
  case 'generic.page.lookupPage':     
    result = userinfo.right.includes('meta.lookup.view');     
    break; 
  case 'generic.page.pagePage':     
    result = userinfo.right.includes('meta.page.view');     
    break; 
  case 'generic.page.documentationPage':
    result = userinfo.right.includes('meta.documentation.view');     
    break; 
  case 'generic.page.subscriptionPage':     
    result = userinfo.right.includes('meta.subscription.view');     
    break; 
  case 'generic.page.notificationPage':     
    result = userinfo.right.includes('meta.notification.view');     
    break; 
  case 'generic.page.statisticPage':     
    result = userinfo.right.includes('meta.statistic.view');     
    break; 
  default:     
    result = userinfo.right.includes(right); 
} 

return result;

