function mergeAssociations(existingList, newList, keyFunc, removeFunc, addFunc, updateFunc) {
  var removed = existingList.filter(function(c) {
    return newList.filter(function(d) {
      return keyFunc(c) === keyFunc(d)
    }).length === 0;
  });
  var added = newList.filter(function(c) {
    return existingList.filter(function(d) {
      return keyFunc(c) === keyFunc(d)
    }).length === 0;
  });
  var changed = newList.filter(function(c) {
    return existingList.filter(function(d) {
      return keyFunc(c) === keyFunc(d)
    }).length > 0;
  });
  removed.forEach(function(f) {
    removeFunc(f);
  });
  added.forEach(function(f) {
    addFunc(f);
  });
  if (updateFunc) {
    changed.forEach(function(f) {
      updateFunc(f);
    });
  }
}
